$("#contactForm").validator().on("submit", function (event) {
    if (event.isDefaultPrevented()) {
        // handle the invalid form...
        formError();
        submitMSG(false, "Did you enter the correct email?");
    } else {
        // everything looks good!
        event.preventDefault();
        submitForm();
    }
});

function submitForm(){
    var email = $("#email").val();

    if (email) {
        db.collection("emails").add({
            email: email,
            //date: new Date().toDateString(),
            timestamp: firebase.firestore.Timestamp.now()
        })
        .then((docRef) => {
            //console.log("Document written with ID: ", docRef.id);
            formSuccess();
        })
        .catch((error) => {
            console.error("Error adding document: ", error);
            formError();
        });
    }
    else {
        formError();
    }
}

function formSuccess(){
    $("#contactForm")[0].reset();
    submitMSG(true, "We got your email!");
}

function formError(){
    $("#contactForm").removeClass().addClass('shake animated').one('webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend', function(){
        $(this).removeClass();
    });
}

function submitMSG(valid, msg){
    if(valid){
        var msgClasses = "h3 text-center tada animated text-success font-weight-bold";
    } else {
        var msgClasses = "h3 text-center text-danger front-weight-bold";
    }
    $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}


